paragraphs.text:
%hive
select avg(amount)
    from orders;

%hive
select part, sum(amount) over (partition by part)
    from orders;

%hive
select distinct part, SUM(amount) over (partition by part)
    from orders;

%hive
select part, customer, amount, MAX(amount) over (partition by part)
    from orders;

%hive
select part, amount, SUM(amount) over (partition by part)
from orders;

%hive
select part, amount, SUM(amount) over (partition by part order by amount)
from orders;

%hive
select part, amount, SUM(amount) over (partition by part  order by amount, oyear, omonth, oday)
from orders;

%hive
select part, amount, AVG(amount) over (partition by part order by oyear, omonth, oday)
from orders;

%hive
select part, amount, LAG(amount) over (partition by part order by oyear, omonth, oday)
from orders;

%hive
select part, amount, amount-LAG(amount) over(partition by part order by oyear, omonth, oday)
from orders;
